export const getObjectivesByTier = (tier: ObjectiveTier): Objective[] => {
  return ALL_OBJECTIVES.filter(obj => obj.tier === tier);
};

export const getObjectiveById = (id: string): Objective | undefined => {
  return ALL_OBJECTIVES.find(obj => obj.id === id);
};

export const getTotalObjectives = (): number => ALL_OBJECTIVES.length;
